sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here
    var rutCompleto = current.u_slx_n_identificacion;

    var Fn = {
        // Valida el rut con su cadena completa "XXXXXXXX-X"
        validaRut: function(rutCompleto) {
            if (!/^[0-9]+[-|‐]{1}[0-9kK]{1}$/.test(rutCompleto)) {
                return false;
            }
            var tmp = rutCompleto.split('-');
            var digv = tmp[1];
            var rut = tmp[0];
            if (digv == 'K') {
                digv = 'k';
            }
            return (Fn.dv(rut) == digv);
        },
        dv: function(T) {
            var M = 0,
                S = 1;
            for (; T; T = Math.floor(T / 10)) {
                S = (S + T % 10 * (9 - M++ % 6)) % 11;
            }
            return S ? S - 1 : 'k';
        }
    };
    // Uso de la función
    //alert( Fn.validaRut(rutCompleto) ? 'Valido' : 'inválido');
    //var alerta; = Fn.validaRut(rutCompleto) ? '' : g_form.addErrorMessage('El rut ingresado no es inválido');
    if (Fn.validaRut(rutCompleto)) {

    } else {
		gs.addErrorMessage('El Rut "' + rutCompleto + '" no es válido');
        current.setAbortAction(true);
    }


})(current, previous);
