sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here
    var fecha_ce = new GlideDateTime(current.u_slx_evento_de_transito.u_slx_fecha_aviso_ce);
    var fecha_aviso = new GlideDateTime(current.u_slx_hora_aviso_movil);
    var fecha_llegada = new GlideDateTime(current.u_slx_hora_llegada_movil);
    var fecha_termino = new GlideDateTime(current.u_slx_hora_termino_movil);
    var gtd = new GlideDateTime();

    if (fecha_ce == null || fecha_ce == '') {
        gs.addErrorMessage("La Fecha de Aviso CE se encuentra vacía, se debe ingresar antes de llenar las horas");
        current.setAbortAction(true);
    }

    if (fecha_ce.compareTo(fecha_aviso) >= 0) {
        gs.addErrorMessage("La fecha de aviso debe ser posterior o igual a la Fecha de Aviso CE");
        current.setAbortAction(true);
    }

    if (fecha_aviso.compareTo(fecha_llegada) == 1) {
        gs.addErrorMessage("La fecha de llegada debe ser posterior a la Hora de Aviso");
        current.setAbortAction(true);
    }

    if (fecha_llegada.compareTo(fecha_termino) == 1) {
        gs.addErrorMessage("La fecha término debe ser posterior a la Hora de Llegada");
        current.setAbortAction(true);
    }

    if (gtd.compareTo(fecha_termino) == -1) {
        gs.addErrorMessage("La Fecha y hora de término debe ser anterior a la actual");
        current.setAbortAction(true);
    }

})(current, previous);
